WITH latest AS (
  SELECT project_key, metric_key, metric_value,
    ROW_NUMBER() OVER (PARTITION BY project_key, metric_key ORDER BY recorded_at DESC) as rn
  FROM sonarqube_metrics WHERE metric_key = 'security_rating'
)
SELECT COUNT(*) as value FROM latest WHERE rn = 1 AND metric_value >= 4;

WITH latest AS (
  SELECT project_key, metric_value,
    ROW_NUMBER() OVER (PARTITION BY project_key ORDER BY recorded_at DESC) as rn
  FROM sonarqube_metrics WHERE metric_key = 'sqale_index'
)
SELECT ROUND((SUM(metric_value) / 480.0)::numeric, 1) as value FROM latest WHERE rn = 1;

WITH latest AS (
  SELECT project_key, metric_value,
    ROW_NUMBER() OVER (PARTITION BY project_key ORDER BY recorded_at DESC) as rn
  FROM sonarqube_metrics WHERE metric_key = 'coverage'
)
SELECT ROUND(AVG(metric_value)::numeric, 1) as value FROM latest WHERE rn = 1;
